     streetSuffix        VARCHAR2, side                VARCHAR2,
     percent             NUMBER, edgeId              NUMBER,
     errorMessage        VARCHAR2, matchcode           NUMBER,
     matchmode VARCHAR2, longitude  NUMBER, latitude   NUMBER)
  RETURN SELF AS RESULT AS
