    RETURN TREAT(g AS ST_Curve).ST_Length();
  END IF;
  IF(nls_upper(OGC_GeometryType(g)) IN ('MULTILINESTRING', 'ST_MULTILINESTRING')) THEN
    RETURN TREAT(g AS ST_MultiCurve).ST_Length();
  END IF;
  RETURN NULL;
END OGC_Length;
